    $(".J_tabRight").on("click", b);
    $("#layoutBody").delegate(".J_menuTab","contextmenu",function(){return false;});
    $("#layoutBody").delegate(".J_menuTab","mousedown",function(e){
        var top = e.pageY;
        var left = e.pageX;
        if(3 == e.which){
            if($(".dropdown-menu").length > 0){
                $(".dropdown-menu").hide();
                $(".dropdown-menu").css({"top":top+"px","left":left+"px"}).show();
                $("#layoutBody").data("contextMenuTarget",$(e.target));
            }
        }
    })
    $("#layoutBody").delegate(".dropdown-menu","mousemove",function(e){cxtHover=false});
    $("#layoutBody").delegate(".dropdown-menu","mouseout",function(e){cxtHover=true});
    $("#layoutBody").on("click",function(){
        if(cxtHover){
            if($(".dropdown-menu").is(":visible")) $(".dropdown-menu").hide();
        }
    })
    $(".J_tabCloseAll").on("click",
    function() {
        $(".page-tabs-content").children("[data-id]").not(":first").each(function() {
            $('.J_iframe[data-id="' + $(this).data("id") + '"]').remove();
            $(this).remove()
        });
        $(".page-tabs-content").children("[data-id]:first").each(function() {
            $('.J_iframe[data-id="' + $(this).data("id") + '"]').show();
            $(this).addClass("active")
        });
        $(".page-tabs-content").css("margin-left", "0");
        
        
        $(".dropdown-menu").hide();
        $("#layoutBody").data("contextMenuTarget","");
    })

    var dept_size=sessionStorage.getItem('cur_org_size');
    $('.del_dept').click(function(){
        if(dept_size<=0){
            confirmMsg('你确定删除吗？',function(dialog){
                dialog.close();
                $.ajax({
                    type:'post',
                    url:'/crm/user/user/deleteDeptdata.json',
                    dataType:'json',
                    data:{'deptId':dept_id},
                    success:function(data){
                        console.info(data)
                        if(data.code==0){
                            returnMessage(1,'删除成功');
                            closeTab();
                        }else{
                            returnMessage(2,'删除失败！');
                        }
                    },
                    error:function(data){
                        returnMessage(200,'删除失败！');
                    }
                })
            })
        }
        else{
               returnMessage(2,'请先把部门内的成员删除！') 
        }  
    })   
